#include "model/Database.hpp"
#include "model/Protocol.hpp"
#include "model/Language.hpp"

using namespace model;

/**
 * A subset of technologies I used for backend development so far.
 */
class BackendDevelopment {

    std::vector<Language*> getLanguages() {
        std::vector<Language*> languages;

        languages.push_back(new Language("C++"))
        languages.push_back(new Language("Dart"))
        languages.push_back(new Language("TypeScript"))

        return languages;
    }

    std::vector<Database*> getDatabases() {
        std::vector<Database*> databases;

        databases.push_back(new Database("MongoDB"))
        databases.push_back(new Database("Postgres"))

        return databases;
    }

    std::vector<Protocol*> getProtocols() {
        std::vector<Protocol*> protocols;

        protocols.push_back(new Protocol("WebSocket"))
        protocols.push_back(new Protocol("REST"))
        protocols.push_back(new Protocol("GraphQL"))

        return protocols;
    }

}

